   $('#tampil_tingkat').html("<h4 class='text-center animation animating pulse'>Daftar Tingkat SMP</h4>");    $('#tampil_tingkat').append('<div class="tab-content">'               + '<div class="tab-pane active" id="tingkatSD">'+                    '<table class="table table-striped" id="zero-configuration" style="font-size: 13px">'+                       ' <thead>'+                            '<tr>'+                                '<th class="text-center">NO</th>'+                                '<th>Nama Mata Pelajaran</th>'+                                '<th>Keterangan</th>'+                                '<th>BAB</th>'+                                '<th class="text-center">Aksi</th>'+                            '</tr>'+                        '</thead>'+'<tbody> </tbody>');        table = $('#zero-configuration').DataTable({        "ajax" : {            "url" : "<?=base_url()?>index.php/admin/daftar_smp",            "type" : "POST"        },        "emptyTable": "Tidak Ada Data Pesan",        "info": "Menampilkan _START_ sampai _END_ dari _TOTAL_ entries",    });        table.order([3,"desc"]);    }    function load_sma(){           $('#tampil_tingkat').html("<h4 class='text-center animation animating pulse'>Daftar Tingkat SMA</h4>");    $('#tampil_tingkat').append('<div class="tab-content">'               + '<div class="tab-pane active" id="tingkatSD">'+                    '<table class="table table-striped" id="zero-configuration" style="font-size: 13px">'+                       ' <thead>'+                            '<tr>'+                                '<th class="text-center">NO</th>'+                                '<th>Nama Mata Pelajaran</th>'+                                '<th>Keterangan</th>'+                                '<th>BAB</th>'+                                '<th class="text-center">Aksi</th>'+                            '</tr>'+                        '</thead>'+'<tbody> </tbody>');           table = $('#zero-configuration').DataTable({        "ajax" : {            "url" : "<?=base_url()?>index.php/admin/daftar_sma",            "type" : "POST"        },        "emptyTable": "Tidak Ada Data Pesan",        "info": "Menampilkan _START_ sampai _END_ dari _TOTAL_ entries",    });           table.order([3,"desc"]);    }    function load_smaipa(){           $('#tampil_tingkat').html("<h4 class='text-center animation animating pulse'>Daftar Tingkat SMA IPA</h4>");    $('#tampil_tingkat').append('<div class="tab-content">'               + '<div class="tab-pane active" id="tingkatSD">'+                    '<table class="table table-striped" id="zero-configuration" style="font-size: 13px">'+                       ' <thead>'+                            '<tr>'+                                '<th class="text-center">NO</th>'+                                '<th>Nama Mata Pelajaran</th>'+                                '<th>Keterangan</th>'+                                '<th>BAB</th>'+                                '<th class="text-center">Aksi</th>'+                            '</tr>'+                        '</thead>'+'<tbody> </tbody>');      table = $('#zero-configuration').DataTable({        "ajax" : {            "url" : "<?=base_url()?>index.php/admin/daftar_smaipa",            "type" : "POST"        },        "emptyTable": "Tidak Ada Data Pesan",        "info": "Menampilkan _START_ sampai _END_ dari _TOTAL_ entries",    });      table.order([3,"desc"]);    }    function load_smaips(){           $('#tampil_tingkat').html("<h4 class='text-center animation animating pulse'>Daftar Tingkat SMA IPS</h4>");    $('#tampil_tingkat').append('<div class="tab-content">'               + '<div class="tab-pane active" id="tingkatSD">'+                    '<table class="table table-striped" id="zero-configuration" style="font-size: 13px">'+                       ' <thead>'+                            '<tr>'+                                '<th class="text-center">NO</th>'+                                '<th>Nama Mata Pelajaran</th>'+                                '<th>Keterangan</th>'+                                '<th>BAB</th>'+                                '<th class="text-center">Aksi</th>'+                            '</tr>'+                        '</thead>'+'<tbody> </tbody>');         table = $('#zero-configuration').DataTable({        "ajax" : {            "url" : "<?=base_url()?>index.php/admin/daftar_smaips",            "type" : "POST"        },        "emptyTable": "Tidak Ada Data Pesan",        "info": "Menampilkan _START_ sampai _END_ dari _TOTAL_ entries",    });         table.order([3,"desc"]);    }    function tambah_tingkat(){        var   uri = "<?= base_url('index.php/admin/tambahtingkatMP') ?>";        var idmp = $('select[name="idMP"]').val();        var idtingkatMP = $('select[name="idTingkatMP"]').val();        var Keterangan = $('input[name="keterangan"]').val();        if(idmp == 0){            swal('Lengkapi data');        }else if(idtingkatMP == 0){            swal('Lengkapi data');        }else if(Keterangan == ""){            swal('lengkapi data');        }else{        $.ajax({            url:uri,            type:"POST",            data:$('#form_tambahtingkat').serialize(),            typeData:"JSON",            success:function(){            swal("Berhasil ditambahkan");            $('select[name="idMP"]').val(0);            $('select[name="idTingkatMP"]').val(0);            $('input[name="keterangan"]').val("");            $('#myModal').modal('hide');            table.ajax.reload();            },            error:function(){                alert('error');            }        })    }    }        function ajax_edit_tingkat(id){        $.ajax({            url:"<?= base_url('index.php/admin/get_edit_tingkat/') ?>"+id,            type:"GET",            dataType:"JSON",            success:function(data){            $('.modal-title').text("Edit");            $('input[name="id1"]').val(data.id)            $('select[name="idMP1"]').val(data.matapelajaranID);            $('select[name="idTingkatMP1"]').val(data.tingkatID);            $('input[name="keterangan1"]').val(data.keterangan);            $('#modal_rubah').modal('show');            },            error:function(){                alert("error");            }        });    }       function edit_tingkat(){        var uri;            uri = "<?= base_url('index.php/admin/edittingkatMP') ?>";                $.ajax({            url:uri,            type:"POST",            data:$('#form_rubahtingkat').serialize(),            typeData:"JSON",            success:function(){            $('select[name="idMP1"]').val(0);            $('select[name="idTingkatMP1"]').val(0);            $('input[name="keterangan1"]').val("");            $('#modal_rubah').modal('hide');            table.ajax.reload();            swal("Berhasil diedit");            },            error:function(){                alert('error');            }        })    }    function ajax_hapus_tingkat(id){               swal({            title: "Anda Akan Menghapus Tingkat Mata Pelajaran Ini?",            type: "warning",            showCancelButton: true,            confirmButtonColor: "#DD6B55",            confirmButtonText: "Ya, Hapus",            cancelButtonText: "Tidak, batalan",            closeOnConfirm: false,            closeOnCancel: false    },function(isConfirm){        if(isConfirm){            $.ajax({            url:"<?= base_url('index.php/admin/hapustingkatMP') ?>/"+id,            data:$("#form_hapus").serialize(),            type:"POST",            dataType:"JSON",            success:function(){            swal("Berhasil dihapus");            table.ajax.reload();                   },            error:function(){                alert("error");            }        });        }else{            swal("gagal dihapus");        }        }        )    }    </script>